use PokeMonDatabase

CREATE TABLE Pokemon(
PokeDexNumber INT NOT NULL,
PokeDexName   VARCHAR(50) NOT NULL,
TypeId1 INT NOT NULL,
TypeId2 INT NOT NULL,
FOREIGN KEY(TypeId1) REFERENCES Type1(TypeID),
FOREIGN KEY(TypeId2) REFERENCES Type2(TypeID),
PRIMARY KEY(PokeDexNumber)
);

CREATE TABLE Type1(
TypeId INT NOT NULL,
Type1 VARCHAR(50) NOT NULL,
PRIMARY KEY(TypeID)
);

CREATE TABLE Type2(
TypeId INT NOT NULL,
Type2 VARCHAR(50) NOT NULL,
PRIMARY KEY(TypeID)
);

ALTER TABLE Type2
ALTER COLUMN Type2 VARCHAR(50) NULL;


CREATE TABLE BaseStats(
PokeDexNumber INT NOT NULL,
HP  INT NOT NULL,
Attack INT NOT NULL,
Defense INT NOT NULL,
SpecialAttack INT NOT NULL,
SpecialDefense INT NOT NULL,
Speed INT NOT NULL
FOREIGN KEY(PokeDexNumber) REFERENCES Pokemon(PokeDexNumber),
PRIMARY KEY(PokeDexNumber)
);

